import React, { useState } from 'react';
import DateSelector from './components/DateSelector';
import AirportSelector from './components/AirportSelector';
import CitySelector from './components/CitySelector';
import Results from './components/Results';

const App = () => {
  const [dates, setDates] = useState({ departure: '', return: '', flexibility: 0 });
  const [selectedAirports, setSelectedAirports] = useState([]);
  const [cities, setCities] = useState([]);

  const handleDatesChange = (newDates) => {
    setDates(newDates);
  };

  const handleAirportsChange = (newAirports) => {
    setSelectedAirports(newAirports);
  };

  const handleCitiesChange = (newCities) => {
    setCities(newCities);
  };

  return (
    <div>
      <h1>Cheapest Travel Route</h1>
      <DateSelector onChange={handleDatesChange} />
      <AirportSelector onChange={handleAirportsChange} />
      <CitySelector onChange={handleCitiesChange} />
      <Results dates={dates} airports={selectedAirports} cities={cities} />
    </div>
  );
};

export default App;
