import React, { useState } from 'react';

const airports = [
  { name: 'Grenoble Airport', size: 'small', distance: 45 },
  { name: 'Chambery Airport', size: 'small', distance: 50 },
  { name: 'Lyon Airport', size: 'large', distance: 100 },
  { name: 'Geneva Airport', size: 'large', distance: 120 },
  { name: 'Marseille Airport', size: 'large', distance: 300 },
  { name: 'Paris Airport', size: 'large', distance: 600 },
  { name: 'Turin Airport', size: 'medium', distance: 200 },
  { name: 'Milan Airport', size: 'large', distance: 300 },
];

const AirportSelector = ({ onChange }) => {
  const [selected, setSelected] = useState([]);

  const handleChange = (event) => {
    const value = event.target.value;
    setSelected(
      selected.includes(value) ? selected.filter((airport) => airport !== value) : [...selected, value]
    );
  };

  const handleSubmit = () => {
    onChange(selected);
  };

  return (
    <div>
      <h2>Select Airports</h2>
      {airports.map((airport) => (
        <div key={airport.name}>
          <input
            type="checkbox"
            value={airport.name}
            checked={selected.includes(airport.name)}
            onChange={handleChange}
          />
          {airport.name} ({airport.size}, {airport.distance} km)
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default AirportSelector;
