import React, { useState } from 'react';
import './App.css';

const travelData = [
  {
    "departure city": "Paris",
    "airports": [
      { "airportName": "Charles de Gaulle Airport", "distance": "25KM" },
      { "airportName": "Orly Airport", "distance": "14KM" },
      { "airportName": "Beauvais-Tillé Airport", "distance": "85KM" },
      { "airportName": "Le Bourget Airport", "distance": "13KM" }
    ],
    "destinations": [
      {
        "city": "London",
        "airports": [
          { "airportName": "Heathrow Airport", "distance": "24KM" },
          { "airportName": "Gatwick Airport", "distance": "45KM" },
          { "airportName": "Stansted Airport", "distance": "63KM" },
          { "airportName": "Luton Airport", "distance": "55KM" }
        ]
      },
      {
        "city": "Rome",
        "airports": [
          { "airportName": "Fiumicino Airport", "distance": "32KM" },
          { "airportName": "Ciampino Airport", "distance": "15KM" },
          { "airportName": "Perugia San Francesco d'Assisi Airport", "distance": "164KM" },
          { "airportName": "Pescara Abruzzo Airport", "distance": "208KM" }
        ]
      },
      {
        "city": "Berlin",
        "airports": [
          { "airportName": "Berlin Brandenburg Airport", "distance": "22KM" },
          { "airportName": "Leipzig/Halle Airport", "distance": "150KM" },
          { "airportName": "Dresden Airport", "distance": "172KM" },
          { "airportName": "Erfurt-Weimar Airport", "distance": "235KM" }
        ]
      }
    ]
  },
  {
    "departure city": "London",
    "airports": [
      { "airportName": "Heathrow Airport", "distance": "24KM" },
      { "airportName": "Gatwick Airport", "distance": "45KM" },
      { "airportName": "Stansted Airport", "distance": "63KM" },
      { "airportName": "Luton Airport", "distance": "55KM" }
    ],
    "destinations": [
      {
        "city": "Paris",
        "airports": [
          { "airportName": "Charles de Gaulle Airport", "distance": "25KM" },
          { "airportName": "Orly Airport", "distance": "14KM" },
          { "airportName": "Beauvais-Tillé Airport", "distance": "85KM" },
          { "airportName": "Le Bourget Airport", "distance": "13KM" }
        ]
      },
      {
        "city": "Rome",
        "airports": [
          { "airportName": "Fiumicino Airport", "distance": "32KM" },
          { "airportName": "Ciampino Airport", "distance": "15KM" },
          { "airportName": "Perugia San Francesco d'Assisi Airport", "distance": "164KM" },
          { "airportName": "Pescara Abruzzo Airport", "distance": "208KM" }
        ]
      },
      {
        "city": "Berlin",
        "airports": [
          { "airportName": "Berlin Brandenburg Airport", "distance": "22KM" },
          { "airportName": "Leipzig/Halle Airport", "distance": "150KM" },
          { "airportName": "Dresden Airport", "distance": "172KM" },
          { "airportName": "Erfurt-Weimar Airport", "distance": "235KM" }
        ]
      }
    ]
  }
];

function App() {
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedAirport, setSelectedAirport] = useState('');
  const [destinationCities, setDestinationCities] = useState('');
  const [travelPlan, setTravelPlan] = useState([]);

  const handleCityChange = (e) => {
    setSelectedCity(e.target.value);
    setSelectedAirport('');
    setTravelPlan([]);
  };

  const handleAirportChange = (e) => {
    setSelectedAirport(e.target.value);
    setTravelPlan([]);
  };

  const handleDestinationChange = (e) => {
    setDestinationCities(e.target.value);
  };

  const handleGeneratePlan = () => {
    const destinations = destinationCities.split(',').map(city => city.trim());
    const plan = [];

    for (let i = 0; i < destinations.length; i++) {
      const departureCity = i === 0 ? selectedCity : destinations[i - 1];
      const destinationCity = destinations[i];

      const departureData = travelData.find(city => city["departure city"] === departureCity);
      if (departureData) {
        const destinationData = departureData.destinations.find(dest => dest.city === destinationCity);
        if (destinationData) {
          const airport = destinationData.airports[0]; 
          plan.push({
            from: departureCity,
            departureAirport: selectedAirport,
            to: destinationCity,
            airport: airport.airportName,
            distance: airport.distance
          });
        }
      }
    }

    setTravelPlan(plan);
  };

  const cities = travelData.map(city => city["departure city"]);
  const airports = selectedCity ? travelData.find(city => city["departure city"] === selectedCity).airports : [];

  return (
    <div className="App">
      <h1>Travel Planner</h1>
      <div>
        <label>
          Departure City:
          <select value={selectedCity} onChange={handleCityChange}>
            <option value="" disabled>Select a city</option>
            {cities.map(city => (
              <option key={city} value={city}>
                {city}
              </option>
            ))}
          </select>
        </label>
      </div>
      {selectedCity && (
        <div>
          <label>
            Departure Airport:
            <select value={selectedAirport} onChange={handleAirportChange}>
              <option value="" disabled>Select an airport</option>
              {airports.map(airport => (
                <option key={airport.airportName} value={airport.airportName}>
                  {airport.airportName} ({airport.distance})
                </option>
              ))}
            </select>
          </label>
        </div>
      )}
      {selectedAirport && (
        <div>
          <label>
            Enter destination cities (comma-separated):
            <input type="text" value={destinationCities} onChange={handleDestinationChange} />
          </label>
          <button onClick={handleGeneratePlan}>Generate Travel Plan</button>
        </div>
      )}
      {travelPlan.length > 0 && (
        <div>
          <h2>Your Travel Plan</h2>
          {travelPlan.map((leg, index) => (
            <div key={index}>
              <p>From: {leg.from} ,Airport {leg.departureAirport}  To: {leg.to}</p>
              <p>Land at: {leg.airport} ({leg.distance})</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
